--
-- Create procedure `Proc_Report_Today`
--
CREATE PROCEDURE Proc_Report_Today ()
SQL SECURITY INVOKER
BEGIN
  SET @now = (SELECT
      NOW());
  SET @TotalOrder = (SELECT
      COUNT(1)
    FROM OrderShoes os
  --     WHERE YEAR(os.CreatedDate) = YEAR(@now)
  --     AND MONTH(os.CreatedDate) = MONTH(@now)
  --     AND DAY(os.CreatedDate) = DAY(@now)
  );

  SET @TotalProduct = (SELECT
      SUM(od.Quantity)
    FROM OrderDetail od
      INNER JOIN OrderShoes os
        ON od.OrderID = os.OrderID
  --     WHERE YEAR(os.CreatedDate) = YEAR(@now)
  --     AND MONTH(os.CreatedDate) = MONTH(@now)
  --     AND DAY(os.CreatedDate) = DAY(@now)
  );

  SET @TotalMoney = (SELECT
      SUM(os.TotalBill)
    FROM OrderShoes os
  --     WHERE YEAR(os.CreatedDate) = YEAR(@now)
  --     AND MONTH(os.CreatedDate) = MONTH(@now)
  --     AND DAY(os.CreatedDate) = DAY(@now)
  );

  SET @TotalCustomer = (SELECT
      COUNT(1)
    FROM (SELECT DISTINCT
        os.UserID
      FROM OrderShoes os
    --     WHERE YEAR(os.CreatedDate) = YEAR(@now)
    --     AND MONTH(os.CreatedDate) = MONTH(@now)
    --     AND DAY(os.CreatedDate) = DAY(@now)
    ) AS temp);

  SELECT
    @TotalOrder AS TotalOrder,
    IFNULL(@TotalProduct, 0) AS TotalProduct,
    IFNULL(@TotalMoney, 0) AS TotalMoney,
    @TotalCustomer AS TotalCustomer;
END
