--
CREATE PROCEDURE Proc_Report_Product (IN v_Year int, IN v_Month int, IN v_Monday int, IN v_Sunday int, IN v_TimeAnalyst int)
SQL SECURITY INVOKER
BEGIN
  IF v_TimeAnalyst = 1
    OR v_TimeAnalyst = 2 THEN
    SELECT
      SUM(od.Quantity) AS Total,
      s.ShoesName AS Title
    FROM OrderDetail od
      INNER JOIN Shoes s
        ON od.ShoesID = s.ShoesID
    WHERE od.OrderID IN (SELECT DISTINCT
        OrderID
      FROM OrderShoes
      WHERE Status IN (1, 2, 4)
      AND YEAR(CreatedDate) = v_Year
      AND MONTH(CreatedDate) = v_Month
      AND DAY(CreatedDate) BETWEEN v_Monday AND v_Sunday
      GROUP BY OrderID,
               DAY(CreatedDate))
    GROUP BY od.ShoesID,
             s.ShoesName;
  ELSEIF v_TimeAnalyst = 3
    OR v_TimeAnalyst = 4 THEN
    SELECT
      SUM(od.Quantity) AS Total,
      s.ShoesName AS Title
    FROM OrderDetail od
      INNER JOIN Shoes s
        ON od.ShoesID = s.ShoesID
    WHERE od.OrderID IN (SELECT
        os.OrderID
      FROM OrderShoes os
      WHERE YEAR(os.CreatedDate) = v_Year
      AND MONTH(os.CreatedDate) = v_Month
      GROUP BY os.OrderID,
               DAY(CreatedDate))
    GROUP BY od.ShoesID,
             s.ShoesName;
  ELSE
    SELECT
      SUM(od.Quantity) AS Total,
      s.ShoesName AS Title
    FROM OrderDetail od
      INNER JOIN Shoes s
        ON od.ShoesID = s.ShoesID
    WHERE od.OrderID IN (SELECT
        os.OrderID
      FROM OrderShoes os
      WHERE YEAR(os.CreatedDate) = v_Year
      GROUP BY os.OrderID,
               MONTH(os.CreatedDate))
    GROUP BY od.ShoesID,
             s.ShoesName;
  END IF;
END
$$

